    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        if(CustomerID_Field.getText().isEmpty()){
            JOptionPane.showMessageDialog(null, "please enter the ID of the Customer you want to delete.");
        }
        else{
            String ID = CustomerID_Field.getText().trim() ;
            Administrator a = new Administrator();
            a.deletecustmer(ID);
            JOptionPane.showMessageDialog(null, "the Customer which ID is: "+ ID +" deleted.");
            CustomerID_Field.setText("");
        }
    }//GEN-LAST:event_jButton3ActionPerformed

