    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        Administrator a = new Administrator();
        String ID = CustomerID_Field.getText().trim();
        String Name = Name_Field.getText().trim() ;
        String Email = Email_Field.getText().trim() ;
        String Password = Password_Field.getText().trim();
        String SSN = SSN_Field.getText().trim();
        String Phone = Phone_Field.getText().trim();
        String Address = Address_Field.getText().trim() ;

        if( CustomerID_Field.getText().isEmpty() || (Name_Field.getText().isEmpty() && Email_Field.getText().isEmpty()&&
            Password_Field.getText().isEmpty() && SSN_Field.getText().isEmpty() &&
            Phone_Field.getText().isEmpty() && Address_Field.getText().isEmpty()) ){
        JOptionPane.showMessageDialog(null, "please enter the empty Fields.\nNote: at least the Customer id and one from other fields");
        }
        else if(!CustomerID_Field.getText().isEmpty() && !a.Customer_isExist(ID)){
            JOptionPane.showMessageDialog(null, "the Customer ID is'n exist in database");
        }
        else if(!a.validateEmail(Email)){
            JOptionPane.showMessageDialog(null, "InValid Email");
        }
        else if(!a.validPassword(Password)){
            JOptionPane.showMessageDialog(null, "InValid Password");
        }
        else if(!a.validPhone(Phone)){
            JOptionPane.showMessageDialog(null, "InValid Phone");
        }
        else{
            if(!Name_Field.getText().isEmpty()){
                a.editName_in_Customer(ID, Name);
                Name_Field.setText("");
            }
            if(!Email_Field.getText().isEmpty()){
                a.editEmail_in_Customer(ID, Email);
                Email_Field.setText("");
            }
            if(!Password_Field.getText().isEmpty()){
                a.editPassword_in_Customer(ID, Password);
                Password_Field.setText("");
            }
            if(!SSN_Field.getText().isEmpty()){
                a.editSSN_in_Customer(ID, SSN);
                SSN_Field.setText("");
            }
            if(!Phone_Field.getText().isEmpty()){
                a.editPhone_in_Customer(ID, Phone);
                Phone_Field.setText("");
            }
            if(!Address_Field.getText().isEmpty()){
                a.editAddress_in_Customer(ID, Address);
                Address_Field.setText("");
            }
            CustomerID_Field.setText("");
            JOptionPane.showMessageDialog(null, "the Customer is Edited.");
        }
    }//GEN-LAST:event_jButton1ActionPerformed

