    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
        Administrator a = new Administrator();
        ResultSet r1 = a.printAllCustomers();
        DefaultTableModel tableModel = (DefaultTableModel) jTable1.getModel();
        tableModel.setColumnIdentifiers(new String[] { "ID","Name","Email", "Password", "SSN", "Address", "Phone"});
        tableModel.setRowCount(0); // Clear existing data

        try {
            while (r1.next()) {
                Object[] rowData = new Object[8];
                rowData[0] = r1.getInt("ID");
                rowData[1] = r1.getString("Name");
                rowData[2] = r1.getString("Email");
                rowData[3] = r1.getString("Password");
                rowData[4] = r1.getString("SSN");
                rowData[5] = r1.getString("Address");
                rowData[6] = r1.getString("Phone");

                tableModel.addRow(rowData);
            }
        }catch(SQLException e){
            e.printStackTrace();
        }
        JTable table = new JTable(tableModel);
        JScrollPane scrollPane = new JScrollPane(table);
                                            
    }//GEN-LAST:event_jButton4ActionPerformed

